meta.script:
jQuery(document).ready(function(){
var copyright_text = "Liceo Scientifico Tassoni";
var cyberchimps_link = "";
var siteurl = "http://www.liceotassoni.gov.it"; 
if(copyright_text == "")
{
	jQuery(".copyright #copyright_link").text(" "+"Default copyright text");
}
else{ 
	jQuery(".copyright #copyright_link").text(" "+copyright_text);
}
jQuery(".copyright #copyright_link").attr('href',siteurl);
if(cyberchimps_link == 1)
{
	jQuery(".powered").css("display","block");
}
else{
	jQuery(".powered").css("display","none");
}
});

jQuery(document).ready(function($) {

	/* Menu slide toggle */
	$(".widget_nav_menu .widget-title").click(function() {
		$(this).next().children().slideToggle();
	});
	$(".widget_nav_menu .current-menu-item").parent().show();


	/* Nav Search Bar */	
	$('.header-main-right-search .btn-trigger').click(function(){
		$(this).parent('.header-main-right-search').find('#searchform').fadeToggle("slow");
		$(this).toggleClass('fa-times');
	});


	/* Menu mobile slide toggle */
	/* $(".main-nav ul li.menu-item-has-children a").after("+");
	$(".main-nav ul li.menu-item-has-children span.btn_plus").click(function() {
		$(this).next().slideToggle();				
		var val = $(this).text() == "--" ? "+" : "--";
		$(this).hide().text(val).fadeIn("fast");			
	}); */

	$(".main-nav ul li.menu-item-has-children a.menu-image-title-after").click(function() {
		$(this).next().slideToggle();						
	});

	$(".main-nav ul li.menu-item-has-children a span").click(function(event) {
		event.stopPropagation();
	});
	// $(".widget_nav_menu .current-menu-item").parent().show();
});

div.script:
var metaslider_4 = function($) {
    $('#metaslider_4').addClass('flexslider'); // theme/plugin conflict avoidance
    $('#metaslider_4').flexslider({ 
        slideshowSpeed:3000,
        animation:"fade",
        controlNav:false,
        directionNav:false,
        pauseOnHover:true,
        direction:"horizontal",
        reverse:false,
        animationSpeed:600,
        prevText:"<",
        nextText:">",
        slideshow:true
    });
};
var timer_metaslider_4 = function() {
    var slider = !window.jQuery ? window.setTimeout(timer_metaslider_4, 100) : !jQuery.isReady ? window.setTimeout(timer_metaslider_4, 1) : metaslider_4(window.jQuery);
};
timer_metaslider_4();

