		var
			t = $(this);

		var el = $('
')
			.addClass(i === 0 ? 'active' : null)
			.appendTo(pg)
		;
	});

    $('.carousel-caption').each(function(){
    	var str = $(this).find('strong'),
        	strt = str.text()
		;
        if(strt === '') {
        	str.hide();
        }
		var sp = $(this).find('span'),
        	spt = str.text()
		;
        if(spt === '') {
        	sp.hide();
        }
    });

    $('.carousel-caption strong').each(function() {

    var
        el = $(this),
        h = el.html().replace(/\*(.*?)\*/g, function(wm, m) {

            return '' + m + '';
        })
    ;

    el.html(h);
});














