$(window).load(function() {

		// nel caso di contenuto in cache (usa il doppio if per non inserire ampersand - cfr. w3c)

	if (document.cookie)
		if (document.cookie.match(/fx-eu-privacy/))
			return;

	var
		lt = String.fromCharCode(60),
		amp = String.fromCharCode(38),
		tpl = $('#eu-privacy-tpl').html().replace(/</g, lt).replace(/>/g, '>').replace(/&/g, amp),
		div = lt + 'div />'
	;

	var d = $(div)
		.attr({
			id: 'eu-privacy'
		})
//		.on('click', '#eu-privacy-close', function() { sc(); d.css('top', - d.height() - 2); return false; }) // 2 per border
		.on('click', '#eu-privacy-close', function() { sc(); d.removeClass('open').addClass('close'); return false; })
		.append(
			$(div).attr('id', 'eu-privacy-inner').html(tpl)
		)
		.appendTo('body')
	;

//	d.css('top', - d.height());

//	setTimeout(function() { d.css('top', 0); }, 500);
	setTimeout(function() { d.addClass('open'); }, 500);

	var sc = function() {

		fjs.ajax.get('euCookie', fjs.noop);
	};
});

});

$().ready(function() {



            $('.LangSwitch span').click(function(e) {
                $('.LangSwitch ol').slideToggle('fast');
            });

            $('[data-toggle=offcanvas]').click(function () {
                $('.row-offcanvas').toggleClass('active')
            });

              $('.Slider').bxSlider({
                slideWidth: 280,
                minSlides: 1,
                maxSlides: 3,
                moveSlides: 1,
                slideMargin: 10,
                auto: true
              });       

            var
                m = $('.ContCorpoPag'),
                v = $('.MenuV'),
                o = $('#offCanvasToggler'),
                f = $('.ContFooter');

            o.on('click', function(){
            	var mh = m.height(),
                	vh = v.height();
                $(this).toggleClass('clicked');
                if ($(this).hasClass('clicked')&& vh>mh)  {
                    f.animate({margin: vh-mh + " 0 0 0"}, 250);
                } 
                else {
                    f.animate({margin: "0"}, 250);
            }});

            $("#map").addClass( "embed-responsive" );
            $(".viewGMap").wrap( "
" );
            $(".viewGMap").addClass( "embed-responsive-item" );
        	$(".gm-style").addClass( "embed-responsive-item" );



			(function() {

				var
					d = document,
					s = 'script',
					js,
					fjs = d.getElementsByTagName(s)[0],
					pn = fjs.parentNode
				;

				js = d.createElement(s);
				js.src = '//platform.twitter.com/widgets.js';

				pn.insertBefore(js, fjs);

				js = d.createElement(s);
				js.src = '//connect.facebook.net/it_IT/all.js#xfbml=1';

				pn.insertBefore(js, fjs);

				js = d.createElement(s);
				js.src = '//apis.google.com/js/plusone.js';

				pn.insertBefore(js, fjs);

			})();


});
