      function Vis(elemento)
      {
        var TIP = new Array("Tu","Do","Al","At","Ge","Es","Ag");
        $("#News_INT_"+TIP[elemento]).show(0);
        for (i=0; i<=6; i++)
        {
          if (i!=elemento) {$("#News_INT_"+TIP[i]).hide(0);};
        }
      }
