body.script:
//

				jQuery(document).ready(function(){

					jQuery('#cookieBar a.more').click(function(e){
						e.preventDefault();

						if(jQuery(this).hasClass('open'))
						{
							jQuery(this).html('Show more').removeClass('open');
							jQuery('.content').slideUp();
						}
						else 
						{
							jQuery(this).html('Show less').addClass('open');
							jQuery('.content').slideDown();
						}

						return false;
					});

					jQuery('#acceptCookies').click(function(e){

						var ua = navigator.userAgent;
						//Check not BB (BB just uses standard non AJAX link)
					    if (!(ua.indexOf('BlackBerry') >= 0))
					    {
					    	e.preventDefault();

							jQuery.ajax({

								url: '/cookiebar/accept',
	            				success: function(data, textStatus)
	            				{
	            					if(data === 'success')
	            					{
										jQuery('#cookieBar').slideUp(500);
	            					}
								}
							});							
						}					


					});
				});


//

jQuery(document).ready(function () {
    var mappa = new GMaps({
        div: '#google-map1',
        lat: 0,
        lng: 0,
        zoom: 0
    });



        GMaps.geocode({
            address: 'Via Giorgio Del Vecchio, 24 - 00166 Roma',
            callback: function (results, status) {
                if (status == 'OK') {
                    var latlng = results[0].geometry.location;
                    mappa.setCenter(latlng.lat(), latlng.lng());
                    //alert (latlng.lat());
                    mappa.addMarker({
                        lat: latlng.lat(),
                        lng: latlng.lng(),
                        infoWindow: {
                            content: 'Antonio Rosmini - Secondaria: Via Giorgio Del Vecchio, 24 - 00166 Roma'
                        }
                    });

                }
            }
        });



        GMaps.geocode({
            address: 'Via Diomede Marvasi, 11 - 00165 Roma',
            callback: function (results, status) {
                if (status == 'OK') {
                    var latlng = results[0].geometry.location;
                    mappa.setCenter(latlng.lat(), latlng.lng());
                    //alert (latlng.lat());
                    mappa.addMarker({
                        lat: latlng.lat(),
                        lng: latlng.lng(),
                        infoWindow: {
                            content: 'Corrado Alvaro - Primaria: Via Diomede Marvasi, 11 - 00165 Roma'
                        }
                    });

                }
            }
        });



    mappa.zoomIn(-2);

});

