jQuery.noConflict();
(function($) {
	$(function() {
		$('.inviaadesione').click(function(){
			switch ($("input[type=radio][name=scelta]:checked").val()){
					case "1":
						s="Si";
						break;
					case "2":
						s="No";
						break;
					case "3":
						s="Presa Visione";
						break;
				}
			var answer = confirm("Circolare "+$(this).attr('rel') +"\nConfermi la scelta:\n\n   " + s +"?")
			if (answer){
				return true;
			}
			else{
				return false;
			}					
		});
 });
})(jQuery);

