( function(backtop) {
backtop(window).scroll(function() {
if(jQuery(this).scrollTop() != 0) {
backtop('#toTop').fadeIn();	
} else {
backtop('#toTop').fadeOut();
}
});
backtop('#toTop').click(function() {
backtop('body,html').animate({scrollTop:0},800);
});

} ) ( jQuery );
