head.script:
jQuery("document").ready(function($){

    var nav = $('#topbar');

    $(window).scroll(function () {
        if ($(this).scrollTop() > 155) {
            nav.addClass("f-nav");
        } else {
            nav.removeClass("f-nav");
        }
    });

});

